package Rep1;

public class exercicioSeix {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 0, numImpar =0;
		while (num<=100) {
			if (num%2==0) {
				System.out.print(num+ " ");
			}
			
			num++;
		
				
		}
		System.out.println("------------------------------------------------------------");
		
		while (numImpar<=100) {
			if (numImpar%2!=0) {
				System.out.print(numImpar+ " ");
			}
				
			numImpar++;
	}
		

}
}
